import React, { useEffect, useState } from "react";
import Character from "./components/Character";
import axios from "axios";
import "./App.css";
import ReactPaginate from "react-paginate";

const headers = {
  Accept: "application/json",
  Authorization: "Bearer Jd_gpfWUgD1E6rTHJ99Z",
};

interface iCharacter {
  id: string;
  name: string;
  race: string;
  gender: string;
  wikiUrl: string;
  birth: string;
}

function App() {
  const [characters, setCharacters] = useState([] as Array<iCharacter>);
  const [loading, setLoading] = useState<boolean>(false);
  const [limit, setLimit] = useState<number>(10);
  const [pageCount, setPageCount] = useState<number>(1);
  const [totalCharacters, setTotalCharacters] = useState<number>(0);

  useEffect(() => {
    const getCharacters = async () => {
      setLoading(true);
      const res = await axios.get(
        `https://the-one-api.dev/v2/character?limit=${limit}&page=1`,
        {
          headers: headers,
        }
      );
      setPageCount(Math.ceil(totalCharacters / limit));
      setCharacters(res.data.docs);
      setLoading(false);
    };
    getCharacters();
  }, [limit]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchCharacters = async (currentPage: any) => {
    const res = await axios.get(
      `https://the-one-api.dev/v2/character?limit=${limit}&page=${currentPage}`,
      {
        headers: headers,
      }
    );
    const data = res;
    return data;
  };

  const fetchTotalCharacters = async () => {
    const res = await axios.get(`https://the-one-api.dev/v2/character`, {
      headers: headers,
    });
    setTotalCharacters(res.data.docs.length);
  };

  const handlePageClick = async (data: any) => {
    let currentPage = data.selected + 1;
    console.log(data.selected);

    const charactersFromApi = await fetchCharacters(currentPage);
    setCharacters(charactersFromApi);
  };

  return (
    <div className="App">
      <h1>Character Names</h1>
      <Character characters={characters} loading={loading} />
      <ReactPaginate pageCount={pageCount} onPageChange={handlePageClick} />
    </div>
  );
}

export default App;
