import React, { useEffect, useState } from "react";
import Character from "./components/Character";
import Pagination from "./components/Pagination";
import axios from "axios";
import "./App.css";

const headers = {
  Accept: "application/json",
  Authorization: "Bearer Jd_gpfWUgD1E6rTHJ99Z",
};

interface iCharacter {
  id: string;
  name: string;
  race: string;
  gender: string;
  wikiUrl: string;
  birth: string;
}

function App() {
  const [chars, setChars] = useState([] as Array<iCharacter>);
  const [loading, setLoading] = useState<boolean>(false);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [charactersPerPage] = useState<number>(10);

  useEffect(() => {
    const fetchChar = async () => {
      setLoading(true);
      const res = await axios.get("https://the-one-api.dev/v2/character", {
        headers: headers,
      });
      setChars(res.data.docs);
      setLoading(false);
    };

    fetchChar();
  }, []);

  // Get current posts
  const indexOfLastChar = currentPage * charactersPerPage;
  const indexOfFirstChar = indexOfLastChar - charactersPerPage;
  const currentCharacters = chars.slice(indexOfFirstChar, indexOfLastChar);

  return (
    <div className="App">
      <h1>Character Names</h1>
      <Character characters={currentCharacters} loading={loading} />
      <Pagination
        charactersPerPage={charactersPerPage}
        totalcharacters={chars.length}
      />
    </div>
  );
}

export default App;
