import React, { useEffect, useState } from "react";
import Table from "./components/Table";
import axios from "axios";
import "./App.css";
import ReactPaginate from "react-paginate";
import SearchBar from "./components/SearchBar";

const headers = {
  Accept: "application/json",
  Authorization: "Bearer Jd_gpfWUgD1E6rTHJ99Z",
};

export interface iCharacter {
  id: string;
  name: string;
  race: string;
  gender: string;
  wikiUrl: string;
  birth: string;
}

function App() {
  const [characters, setCharacters] = useState([] as Array<iCharacter>);
  const [loading, setLoading] = useState<boolean>(false);
  const [displayLimit, setDisplayLimit] = useState<number>(10);
  const [totalCharacters, setTotalCharacters] = useState<number>(0);
  const [serachValue, setSearchValue] = useState<String>();
  const [asc, setAsc] = useState<boolean>(true);

  useEffect(() => {
    const getCharacters = async () => {
      setLoading(true);
      const res = await axios.get(
        `https://the-one-api.dev/v2/character?limit=${displayLimit}&page=1`,
        {
          headers: headers,
        }
      );
      setCharacters(res.data.docs as Array<iCharacter>).sort((a, b) =>
        a.name > b.name ? 1 : -1
      );
      setLoading(false);
    };
    getCharacters();
    fetchTotalCharacters();
  }, [displayLimit]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchCharacters = async (currentPage: number) => {
    const res = await axios.get(
      `https://the-one-api.dev/v2/character?limit=${displayLimit}&page=${currentPage}`,
      {
        headers: headers,
      }
    );
    return res.data.docs;
  };

  const fetchTotalCharacters = async () => {
    const res = await axios.get(`https://the-one-api.dev/v2/character`, {
      headers: headers,
    });
    setTotalCharacters(res.data.docs.length);
  };

  const handlePageClick = async (data: { selected: number }) => {
    let currentPage = data.selected + 1;
    console.log(data.selected);

    const charactersFromApi = await fetchCharacters(currentPage);

    setCharacters(charactersFromApi);
    setAsc(true);
  };

  const handleSortClick = () => {
    setAsc(!asc);
    if (asc) {
      setCharacters(characters.sort((a, b) => (a.name > b.name ? 1 : -1)));
    } else {
      setCharacters(characters.sort((a, b) => (a.name < b.name ? 1 : -1)));
    }
  };

  return (
    <div className="App">
      <h1>Character Names</h1>
      <SearchBar callback={(serachValue: any) => setSearchValue(serachValue)} />
      <Table
        characters={characters}
        loading={loading}
        handleSortClick={handleSortClick}
      />
      <ReactPaginate
        pageCount={Math.ceil(totalCharacters / displayLimit)}
        onPageChange={handlePageClick}
        previousLabel={"previous"}
        nextLabel={"next"}
        breakLabel={"..."}
        marginPagesDisplayed={2}
        pageRangeDisplayed={3}
        containerClassName={"pagination justify-content-center"}
        pageClassName={"page-item"}
        pageLinkClassName={"page-link"}
        previousClassName={"page-item"}
        previousLinkClassName={"page-link"}
        nextClassName={"page-item"}
        nextLinkClassName={"page-link"}
        breakClassName={"page-item"}
        breakLinkClassName={"page-link"}
        activeClassName={"active"}
      />
    </div>
  );
}

export default App;
