import React, { useEffect, useState } from "react";
import Character from "./components/Character";
import axios from "axios";
import "./App.css";

const headers = {
  Accept: "application/json",
  Authorization: "Bearer Jd_gpfWUgD1E6rTHJ99Z",
};

interface iCharacter {
  id: string;
  name: string;
  race: string;
  gender: string;
  wikiUrl: string;
  birth: string;
}

function App() {
  const [characters, setCharacters] = useState([] as Array<iCharacter>);
  const [loading, setLoading] = useState<boolean>(false);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [charactersPerPage, setCharactersPerPage] = useState<number>(10);

  useEffect(() => {
    const fetchCharacters = async () => {
      setLoading(true);
      const res = await axios.get("https://the-one-api.dev/v2/character", {
        headers: headers,
      });
      setCharacters(res.data.docs);
      setLoading(false);
    };

    fetchCharacters();
  }, []);

  // Get current character
  const indexOfLastCharacter = currentPage * charactersPerPage;
  const indexofFirstCharacter = indexOfLastCharacter - charactersPerPage;
  const currentCharacter = characters.slice(
    indexofFirstCharacter,
    indexOfLastCharacter
  );

  return (
    <div className="App">
      <h1>Character Names</h1>
      <Character characters={currentCharacter} loading={loading} />
    </div>
  );
}

export default App;
