import React from "react";

function getCharacters({
  characters,
  loading,
  pageNumber,
  charactersPerPage,
}: any) {
  const pagesVisited = pageNumber * charactersPerPage;

  if (loading) {
    return <h2>Loading...</h2>;
  }

  const displayCharacters = characters.slice(
    pagesVisited,
    pagesVisited + charactersPerPage
  );

  return (
    <ul>
      {displayCharacters.characters.map((character: any) => (
        <li key={character.id}>{character.name}</li>
      ))}
    </ul>
  );
}

export default getCharacters;
