import React, { useEffect, useState } from "react";
import Character from "./components/Character";
import axios from "axios";
import "./App.css";
import Pagination from "./components/Pagination";

const headers = {
  Accept: "application/json",
  Authorization: "Bearer Jd_gpfWUgD1E6rTHJ99Z",
};

interface iCharacter {
  id: string;
  name: string;
  race: string;
  gender: string;
  wikiUrl: string;
  birth: string;
}

function App() {
  const [characters, setCharacters] = useState([] as Array<iCharacter>);
  const [loading, setLoading] = useState<boolean>(false);
  const [displayLimit, setDisplayLimit] = useState<number>(10);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [totalCharacters, setTotalCharacters] = useState<number>(0);

  const fetchTotalCharacters = async () => {
    const res = await axios.get(`https://the-one-api.dev/v2/character`, {
      headers: headers,
    });
    setTotalCharacters(res.data.docs.length);
  };

  useEffect(() => {
    const fetchCharacters = async () => {
      setLoading(true);
      const res = await axios.get(
        `https://the-one-api.dev/v2/character?limit=${displayLimit}&page=${currentPage}`,
        {
          headers: headers,
        }
      );
      setCharacters(res.data.docs);
      setLoading(false);
    };

    fetchCharacters();
    fetchTotalCharacters();
  }, []);

  return (
    <div className="App">
      <h1>Character Names</h1>
      <Character characters={characters} loading={loading} />
      <Pagination totalCharacters={totalCharacters} />
    </div>
  );
}

export default App;
